function select_field(fld,value) {
	var selected_val = 0;
	for(i=0;i<eval('document.formn.select_'+fld+'.options.length'); i++) {
		eval('document.formn.select_'+fld+'.options['+i+'].selected=false');
	}
	
	for(i=0;i<eval('document.formn.select_'+fld+'.options.length'); i++) {
		v= eval('document.formn.select_'+fld+'.options['+i+'].value');
		if (v == value) {
			eval('document.formn.select_'+fld+'.options['+i+'].selected=true');
			selected_val = 1;
			break;
		}//if
	}//for loop

	if (selected_val == 0) {
		fld_length = eval('document.formn.select_'+fld+'.options.length');
		v= eval('document.formn.select_'+fld+'.options['+(fld_length-1)+'].value');
		if (v == "Others") {
			  //eval('document.formn.select_'+fld+'.options['+(fld_length-1)+'].selected=true');
  			//  eval('document.formn.'+fld+'_text.value="'+value+'"');
		}//if
	}//if
}//select_field
	
